// Monaco Editor NLS (National Language Support) message modules.
// These .js files have no type declarations, so we declare them here to avoid TS7016.
// Available locales in monaco-editor 0.55.1:
declare module 'monaco-editor/esm/nls.messages.cs.js'; // Czech
declare module 'monaco-editor/esm/nls.messages.de.js'; // German
declare module 'monaco-editor/esm/nls.messages.es.js'; // Spanish
declare module 'monaco-editor/esm/nls.messages.fr.js'; // French
declare module 'monaco-editor/esm/nls.messages.it.js'; // Italian
declare module 'monaco-editor/esm/nls.messages.ja.js'; // Japanese
declare module 'monaco-editor/esm/nls.messages.ko.js'; // Korean
declare module 'monaco-editor/esm/nls.messages.pl.js'; // Polish
declare module 'monaco-editor/esm/nls.messages.pt-br.js'; // Portuguese (Brazil)
declare module 'monaco-editor/esm/nls.messages.ru.js'; // Russian
declare module 'monaco-editor/esm/nls.messages.tr.js'; // Turkish
declare module 'monaco-editor/esm/nls.messages.zh-cn.js'; // Chinese (Simplified)
declare module 'monaco-editor/esm/nls.messages.zh-tw.js'; // Chinese (Traditional)
